import React, { useState, useEffect } from "react";
import { FaChevronLeft } from "react-icons/fa";
import { FaChevronRight } from "react-icons/fa";
import CardT from "./CardT";
const Testimonials = () => {
  let data = [
    {
      src: "data:image/jpeg;base64,",
      name: "Tim",
      text: "The food here is amazing, and the service is top-notch!",
    },
    {
      src: "data:image/jpeg;base64,",
      name: "Anna",
      text: "Authentic flavors and a friendly staff. A must-visit!",
    },
    {
      src: "data:image/jpeg;base64,",
      name: "Steve",
      text: "Perfect for events. Delicious food and great service.",
    },
    {
      src: "data:image/jpeg;base64,",
      name: "Sarah",
      text: "Hidden gem with fantastic Mediterranean dishes!",
    },
  ];

  const [index, setIndex] = useState(0);
  const [direction, setDirection] = useState("next"); // for animation
  
  const prevTestimonial = () => {
    setDirection("prev");
    setIndex((prev) => (prev - 1 + data.length) % data.length);
  };

  const nextTestimonial = () => {
    setDirection("next");
    setIndex((prev) => (prev + 1) % data.length);
  };
  // Auto slide every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      nextTestimonial();
    }, 4000);
    
    return () => clearInterval(interval); // Cleanup on unmount
  }, []);
  return (
    <section className="py-12 px-4">
      <span className="block mx-auto font-bold mdF w-fit g text-center">
        Testimonials
      </span>

      <div className="w-[90vw] mx-auto flex flex-col items-center gap-6  relative ">
        <div className="relative w-full  h-[360px]">
          {data.map((t, i) => (
            <div
              key={i}
              className={`absolute top-0 left-0 w-full transition-transform duration-500 ease-in-out h-90
                  ${
                    i === index
                      ? "translate-x-0 opacity-100 z-10"
                      : direction === "next"
                      ? "translate-x-full opacity-0"
                      : "-translate-x-full opacity-0"
                  }
                `}
            >
              <CardT {...t} />
            </div>
          ))}
        </div>
    
        {/* Chevron buttons - absolutely positioned */}

        <button
          onClick={prevTestimonial}
          aria-label="Previous testimonial"
          className="cursor-pointer absolute top-1/2 -left-4 -translate-y-[50%] rounded-full bg-black/70 text-gray-200    z-10   text-3xl p-1"
        >
          <FaChevronLeft className="-left-[2px] relative" />
        </button>

        <button
          onClick={nextTestimonial}
          aria-label="Next testimonial"
          className="cursor-pointer absolute top-1/2 -right-4 -translate-y-[50%] rounded-full bg-black/70 text-gray-200  h-left-3  z-10   text-3xl p-1"
        >
          <FaChevronRight className="left-[2px] relative" />
        </button>
      </div>
    </section>
  );
};

export default Testimonials;
